mapper.select:
SELECT C.CLT_NO, C.CLT_NAME, O.ORD_STS, O.ORD_SHP_CAT, O.ORD_NO, OI.OI_NO, OI.OI_URLS_QTY FROM TB_ORD O LEFT JOIN TB_CLT C ON (O.CLT_NO = C.CLT_NO) LEFT JOIN TB_ORD_ITM OI ON (O.ORD_NO = OI.ORD_NO) WHERE O.ORD_SHP_CAT IN (
#{shippingCategory}
) AND O.ORD_STS != 0 AND (OI.OI_URLS_QTY = 0 OR OI.OI_URLS_QTY IS NULL)
AND OI.OI_URLS_QTY IS NOT NULL AND OI.OI_URLS_QTY != 0
AND O.ORD_STS IN (2,3)
AND O.ORD_STS = #{status}
AND O.ORD_NO LIKE CONCAT('%',#{orderNo},'%')
AND C.CLT_NAME LIKE CONCAT('%',#{clientName},'%')
GROUP BY OI.OI_NO ORDER BY O.ORD_STS

SELECT C.CLT_NO, C.CLT_NAME, O.ORD_STS, O.ORD_SHP_CAT, O.ORD_NO, OI.OI_NO, OI.OI_URLS_QTY FROM TB_ORD O LEFT JOIN TB_CLT C ON (O.CLT_NO = C.CLT_NO) LEFT JOIN TB_ORD_ITM OI ON (O.ORD_NO = OI.ORD_NO) WHERE O.ORD_SHP_CAT IN (
#{shippingCategory}
) AND O.ORD_STS != 0 AND OI.OI_URLS_QTY IS NOT NULL AND OI.OI_URLS_QTY != 0
AND O.ORD_STS IN (2,3)
AND O.ORD_STS = #{status}
AND O.ORD_NO LIKE CONCAT('%',#{orderNo},'%')
AND C.CLT_NAME LIKE CONCAT('%',#{clientName},'%')
AND O.ORD_STS != 0 GROUP BY OI.OI_NO ORDER BY O.ORD_STS
