mapper.select:
SELECT OI.OI_NO FROM TB_CMB_SHP CS JOIN TB_ORD_ITM OI ON (CS.OI_NO = OI.OI_NO)
WHERE DATE_FORMAT(CS_RCV_DT, '%Y-%m-%d') >= STR_TO_DATE(#{dateList[0]}, '%Y-%m-%d') AND DATE_FORMAT(CS_RCV_DT, '%Y-%m-%d') <= STR_TO_DATE(#{dateList[1]}, '%Y-%m-%d')
AND CS.CS_RCV_CHK=0 AND CS.CS_DLV_CHK=0 AND CS.EMP_ID=#{employeeId}

SELECT OI.OI_NO FROM TB_CMB_SHP CS JOIN TB_ORD_ITM OI ON (CS.OI_NO = OI.OI_NO)
WHERE DATE_FORMAT(CS_RCV_DT, '%Y-%m-%d') >= STR_TO_DATE(#{dateList[0]}, '%Y-%m-%d') AND DATE_FORMAT(CS_RCV_DT, '%Y-%m-%d') <= STR_TO_DATE(#{dateList[1]}, '%Y-%m-%d')
AND CS.CS_RCV_CHK=1 AND CS.CS_DLV_CHK=0 AND CS.EMP_ID=#{employeeId}

mapper.update:
UPDATE TB_CMB_SHP
SET CS_DLV_QTY=#{deliveryQuantity}
    , CS_DLV_URLS=#{deliveryUnrelease}
    , CS_DLV_CHK=#{deliverCheck}
WHERE OI_NO=#{orderItemNo}
