select N.NO, RI_INFO.* from (select `C`.`CLT_NAME` ,`O`.`ORD_SHP_CAT`,`R`.`RLS_CODE`,`O`.`ORD_SHP_DEST`,`O`.`ORD_NO`,`V`.`V_NAME`,`I`.`ITM_NAME`, I.ITM_CODE, IFNULL(P.PIC_QTY, CSP.CSP_RCV_QTY) AS PIC_QTY, `RI`.`RI_QTY`, E.EMP_NAME, `R`.`RLS_BX_QTY`, IF(O.ORD_STS>4, 'Y', 'N') AS RLS_DN, `OI`.`OI_NT`, OI.OI_NO from (SELECT * FROM TB_ORD WHERE ORD_STS IN (4,5,6)) AS O INNER JOIN TB_ORD_ITM AS OI ON `O`.`ORD_NO` = `OI`.`ORD_NO` INNER JOIN TB_RLS AS R ON `O`.`ORD_NO` = `R`.`ORD_NO` INNER JOIN TB_CLT AS C ON `O`.`CLT_NO` = `C`.`CLT_NO` INNER JOIN TB_ITM AS I ON `OI`.`ITM_CODE` = `I`.`ITM_CODE` INNER JOIN TB_VND AS V ON `I`.`V_NO` = `V`.`V_NO` INNER JOIN TB_RLS_INSP AS RI ON `RI`.`OI_NO` = `OI`.`OI_NO` LEFT JOIN TB_PIC AS P ON `OI`.`OI_NO` = P.OI_NO LEFT JOIN TB_CMB_SHP_PTN AS CSP ON `OI`.`OI_NO` = CSP.OI_NO LEFT JOIN TB_EMP AS E ON P.EMP_ID = E.EMP_ID OR CSP.EMP_ID = E.EMP_ID order by `O`.`ORD_NO` DESC) AS RI_INFO JOIN ( SELECT R.*, @ROWNUM := @ROWNUM+1 AS 'no' FROM (SELECT @ROWNUM := #{startRowIndex}, ORD_NO FROM TB_ORD WHERE ORD_STS IN (4,5,6) AND ORD_SHP_CAT IN
#{arr}
GROUP BY ORD_NO ORDER BY ORD_NO DESC) AS R LIMIT #{startRowIndex},#{rowsPerPage}) N ON RI_INFO.ORD_NO = N.ORD_NO ORDER BY RI_INFO.ORD_NO DESC
